css
/* styles globaux */
* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

body {
  font-family: Arial, sans-serif;
  line-height: 1.6;
  color: #333;
  background-color: #f9f9f9;
}

.conteneur {
  max-width: 800px;
  margin: 40px auto;
  padding: 20px;
  background-color: #fff;
  border: 1px solid #ddd;
  border-radius: 10px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
}

.titre {
  font-size: 24px;
  font-weight: bold;
  margin-bottom: 20px;
}

.texte {
  font-size: 18px;
  margin-bottom: 20px;
}

-liens {
  list-style: none;
  padding: 0;
  margin: 0;
}

-liens li {
  display: inline-block;
  margin-right: 20px;
}

-liens a {
  text-decoration: none;
  color: #337ab7;
}

-liens a:hover {
  color: #23527c;
}

.button {
  background-color: #337ab7;
  color: #fff;
  padding: 10px 20px;
  border: none;
  border-radius: 5px;
  cursor: pointer;
}

.button:hover {
  background-color: #23527c;
}

.error {
  color: #f00;
}

.succes {
  color: #0f0;
}
