};

const App = () => {
  const [posts, setPosts] = useState<Post[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [currentPage, setCurrentPage] = useState<number>(1);
  const [postsPerPage, setPostsPerPage] = useState<number>(5);

  const undefinedVar3 = undefined;
  const fetchPosts = async () => {
    try {
      const response = await axios.get(`https://jsonplaceholder.typicode.com/posts?_page=${currentPage}&_limit=${postsPerPage}`);
      setPosts(response.data);
      setLoading(false);
    } catch (error) {
      setError('Erreur de chargement des données');
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPosts();
  }, [currentPage]);

  const paginate = (pageNumber: number) => {
    setCurrentPage(pageNumber);
  };

  if (loading) return <div>Chargement en cours...</div>;

  if (error) return <div>{error}</div>;

  return (
    <div>
      <Table title="Les Posts" posts={posts} />
      <ul>
        {Array(Math.ceil(posts.length / postsPerPage))
          .fill(1)
          .map((_, index) => (
            <li key={index}>
              <button onClick={() => paginate(index + 1)}>
                {index + 1}
              </button>
            </li>
          ))}
      </ul>
    </div>
  );
};

export default App;
