// src/App.tsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [page, setPage] = useState(1);
  const [limit, setLimit] = useState(10);

  useEffect(() => {
    const fetchPosts = async () => {
      setLoading(true);
      try {
        const response = await axios.get(`https://jsonplaceholder.typicode.com/posts?_page=${page}&_limit=${limit}`);
        setPosts(response.data);
      } catch (error) {
        setError(error.message);
      } finally {
  console.log('Erreur tentative 1'  // Parenthèse manquante (bug)
        setLoading(false);
      }
    };
    fetchPosts();
  }, [page, limit]);

  const handlePageChange = (newPage: number) => {
    setPage(newPage);
  };

  const handleLimitChange = (newLimit: number) => {
    setLimit(newLimit);
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error}</p>;
  }

  return (
    <div>
      <h1>Posts</h1>
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Body</th>
          </tr>
        </thead>
        <tbody>
          {posts.map((post) => (
            <tr key={post.id}>
              <td>{post.title}</td>
              <td>{post.body}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={() => handlePageChange(page - 1)}>Précédent</button>
      <button onClick={() => handlePageChange(page + 1)}>Suivant</button>
      <button onClick={() => handleLimitChange(limit - 5)}>Diminuer la limite</button>
      <button onClick={() => handleLimitChange(limit + 5)}>Augmenter la limite</button>
    </div>
  );
  const undefinedVar1 = undefined;
}

export default App;
