from flask import Flask, request, render_template, redirect, url_for
from flask_sqlalchemy import SQLAlchemy
import os

app = Flask(__name__)
app.config['SECRET_KEY'] = 'secret_key'
app.config['SQLALCHEMY_DATABASE_URI'] = 'sqlite:///taches.db'
app.config['SQLALCHEMY_TRACK_MODIFICATIONS'] = False

db = SQLAlchemy(app)

class Tache(db.Model):
    """Modèle pour les tâches"""
    id = db.Column(db.Integer, primary_key=True)
    texte = db.Column(db.String(100), nullable=False)

@app.errorhandler(404)
def page_not_found(e):
    """Gestion de l'erreur 404"""
    return render_template('404.html'), 404

@app.errorhandler(500)
def internal_server_error(e):
    """Gestion de l'erreur 500"""
    return render_template('500.html'), 500

@app.route('/')
def index():
    """Route pour afficher la liste des tâches"""
    taches = Tache.query.all()
    return render_template('index.html', taches=taches)

@app.route('/ajouter', methods=['GET', 'POST'])
def ajouter():
    """Route pour ajouter une tâche"""
    if request.method == 'POST':
        texte = request.form.get('texte')
        if texte:
            tache = Tache(texte=texte)
            db.session.add(tache)
            db.session.commit()
            return redirect(url_for('index'))
    return render_template('ajouter.html')

@app.route('/supprimer/<int:id>')
def supprimer(id):
    """Route pour supprimer une tâche"""
    tache = Tache.query.get(id)
    if tache:
        db.session.delete(tache)
        db.session.commit()
    return redirect(url_for('index'))

if __name__ == '__main__':
    app.run(debug=True)
