# main.py

from flask import Flask, request, jsonify
import json

app = Flask(__name__)

# Données en mémoire
donnees = {
    "caracteres_unicode": ["°", "±"],
    "noms_variables": ["Étienne", "Antoine"]
}

# Route pour gérer les caractères Unicode
@app.route('/caracteres', methods=['GET'])
def get_caracteres():
    return jsonify(donnees['caracteres_unicode'])

# Route pour gérer les noms de variables avec accents
@app.route('/noms_variables', methods=['GET'])
def get_noms_variables():
    return jsonify(donnees['noms_variables'])

# Route pour ajouter un caractère Unicode
@app.route('/caracteres', methods=['POST'])
def ajout_caractere():
    nouveau_caractere = request.json['caractere']
    donnees['caracteres_unicode'].append(nouveau_caractere)
    return jsonify(donnees['caracteres_unicode'])

# Route pour ajouter un nom de variable
@app.route('/noms_variables', methods=['POST'])
def ajout_nom_variable():
    nouveau_nom = request.json['nom']
    donnees['noms_variables'].append(nouveau_nom)
    return jsonify(donnees['noms_variables'])

# Route pour supprimer un caractère Unicode
@app.route('/caracteres/<string:caractere>', methods=['DELETE'])
def supprimer_caractere(caractere):
    if caractere in donnees['caracteres_unicode']:
        donnees['caracteres_unicode'].remove(caractere)
        return jsonify(donnees['caracteres_unicode'])
    return jsonify({"erreur": "Caractère non trouvé"}), 404

# Route pour supprimer un nom de variable
@app.route('/noms_variables/<string:nom>', methods=['DELETE'])
def supprimer_nom_variable(nom):
    if nom in donnees['noms_variables']:
        donnees['noms_variables'].remove(nom)
        return jsonify(donnees['noms_variables'])
    return jsonify({"erreur": "Nom non trouvé"}), 404

if __name__ == "__main__":
    app.run(debug=True)
