from flask import Flask, request, jsonify
from flask_sqlalchemy import SQLAlchemy

app = Flask(__name__)
app.config["SQLALCHEMY_DATABASE_URI"] = "sqlite:///database.db"
db = SQLAlchemy(app)

class Caractere(db.Model):
    id = db.Column(db.Integer, primary_key=True)
    unicode = db.Column(db.String(10), unique=True, nullable=False)
    nom = db.Column(db.String(50), unique=True, nullable=False)

class Variable(db.Model):
    id = db.Column(db.Integer, primary_key=True)
    nom = db.Column(db.String(50), unique=True, nullable=False)
    accent = db.Column(db.String(10), nullable=False)

@app.route("/unicode", methods=["GET"])
def get_unicode():
    caracteres = Caractere.query.all()
    return jsonify([{"unicode": c.unicode, "nom": c.nom} for c in caracteres])

@app.route("/variables", methods=["GET"])
def get_variables():
    variables = Variable.query.all()
    return jsonify([{"nom": v.nom, "accent": v.accent} for v in variables])

@app.route("/unicode", methods=["POST"])
def add_unicode():
    unicode = request.json["unicode"]
    nom = request.json["nom"]
    caracter = Caractere(unicode=unicode, nom=nom)
    db.session.add(caracter)
    db.session.commit()
    return jsonify({"message": "Caractère ajouté"})

@app.route("/variables", methods=["POST"])
def add_variable():
    nom = request.json["nom"]
    accent = request.json["accent"]
    variable = Variable(nom=nom, accent=accent)
    db.session.add(variable)
    db.session.commit()
    return jsonify({"message": "Variable ajoutée"})

if __name__ == "__main__":
    app.run(debug=True)
