# config.py

import json

class Config:
    def __init__(self):
        self.mots_a_deviner = self.lire_mots_a_deviner()
        self.score = self.lire_score()
        self.niveau = self.lire_niveau()

    def lire_mots_a_deviner(self):
        with open('mots_a_deviner.json', 'r') as f:
            return json.load(f)

    def lire_score(self):
        try:
            with open('score.json', 'r') as f:
                return json.load(f)
        except FileNotFoundError:
            return {'score': 0}

    def lire_niveau(self):
        try:
            with open('niveau.json', 'r') as f:
                return json.load(f)
        except FileNotFoundError:
            return {'niveau': 1}

    def sauvegarder_score(self):
        with open('score.json', 'w') as f:
            json.dump(self.score, f)

    def sauvegarder_niveau(self):
        with open('niveau.json', 'w') as f:
            json.dump(self.niveau, f)
