import streamlit as st
import pandas as pd
import matplotlib.pyplot as plt
import json

# Chargement des données
with open('donnees.json') as f:
    donnees = json.load(f)

# Fonction pour afficher les données
def afficher_donnees():
    st.header('Données')
    st.write(pd.DataFrame(donnees))

# Fonction pour afficher les graphiques
def afficher_graphiques():
    st.header('Graphiques')
    data = pd.DataFrame(donnees)
    fig, ax = plt.subplots()
    ax.bar(data['nom'], data['valeur'])
    st.pyplot(fig)

# Fonction pour la calculatrice
def calculatrice():
    st.header('Calculatrice')
    nombre1 = st.number_input('Nombre 1', value=0)
    nombre2 = st.number_input('Nombre 2', value=0)
    operateur = st.selectbox('Opérateur', ['+', '-', '*', '/'])
    if st.button('Calculer'):
        if operateur == '+':
            result = nombre1 + nombre2
        elif operateur == '-':
            result = nombre1 - nombre2
        elif operateur == '*':
            result = nombre1 * nombre2
        elif operateur == '/':
            if nombre2 != 0:
                result = nombre1 / nombre2
            else:
                result = 'Erreur : division par zéro'
        st.write('Résultat :', result)

# Fonction pour la formulaire
def formulaire():
    st.header('Formulaire')
    nom = st.text_input('Nom')
    prenom = st.text_input('Prénom')
    age = st.number_input('Age', value=0)
    if st.button('Soumettre'):
        st.write('Bonjour', nom, prenom, '!', 'Vous avez', age, 'ans.')

# Fonction pour la table
def table():
    st.header('Table')
    donnees = [(1, 'Nom 1'), (2, 'Nom 2'), (3, 'Nom 3')]
    st.write(pd.DataFrame(donnees, columns=['ID', 'Nom']))

# Fonction pour le filtre
def filtre():
    st.header('Filtre')
    valeurs = donnees['valeur']
    nom = st.selectbox('Nom', valeurs)
    if st.button('Filtrer'):
        st.write('Vous avez sélectionné', nom)

# Fonction pour la page de dashboard
def dashboard():
    st.header('Dashboard')
    st.write('Bienvenue sur notre dashboard')

# Page d'accueil
def page_accueil():
    st.header('Accueil')

# Route pour la page d'accueil
@st.cache
def page_accueil_route():
    page_accueil()

# Route pour la page de calculatrice
@st.cache
def calculatrice_route():
    calculatrice()

# Route pour la page de formulaire
@st.cache
def formulaire_route():
    formulaire()

# Route pour la page de table
@st.cache
def table_route():
    table()

# Route pour la page de filtre
@st.cache
def filtre_route():
    filtre()

# Route pour la page de dashboard
@st.cache
def dashboard_route():
    dashboard()

# Route pour la page de graphiques
@st.cache
def graphiques_route():
    afficher_graphiques()

# Route pour la page de données
@st.cache
def donnees_route():
    afficher_donnees()

# Application Streamlit
def main():
    st.title('Application Streamlit')
    st.sidebar.title('Menu')
    menu = ['Accueil', 'Calculatrice', 'Formulaire', 'Table', 'Filtre', 'Graphiques', 'Données', 'Dashboard']
    choix = st.sidebar.selectbox('Sélectionnez une option', menu)
    if choix == 'Accueil':
        page_accueil_route()
    elif choix == 'Calculatrice':
        calculatrice_route()
    elif choix == 'Formulaire':
        formulaire_route()
    elif choix == 'Table':
        table_route()
    elif choix == 'Filtre':
        filtre_route()
    elif choix == 'Graphiques':
        graphiques_route()
    elif choix == 'Données':
        donnees_route()
    elif choix == 'Dashboard':
        dashboard_route()

if __name__ == "__main__":
    main()
