// src/index.tsx

import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [pag, setPag] = useState(1);
  const [postsPerPage, setPostsPerPage] = useState(10);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get(`https://jsonplaceholder.typicode.com/posts?_page=${pag}&_limit=${postsPerPage}`);
        setData(response.data);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };
    fetchPosts();
  }, [pag]);

  const previousPage = () => {
    if (pag > 1) {
      setPag(pag - 1);
    }
  };

  const nextPage = () => {
    setPag(pag + 1);
  };

  return (
    <div>
      <h1>Tableau de posts</h1>
      {loading ? (
        <p>chargement...</p>
      ) : (
        <div>
          <table>
            <thead>
              <tr>
                <th>Post ID</th>
                <th>Post title</th>
              </tr>
            </thead>
            <tbody>
              {data.map((post, index) => (
                <tr key={index}>
                  <td>{post.id}</td>
                  <td>{post.title}</td>
                </tr>
              ))}
            </tbody>
          </table>
          <button onClick={previousPage}>Précédent</button>
          <button onClick={nextPage}>Suivant</button>
        </div>
      )}
      {error && <p>Erreur : {error}</p>}
    </div>
  );
};

export default App;
