import random
from flask import Flask, render_template, request

app = Flask(__name__)

# Liste de mots pour le jeu
mots = ["python", "flask", "jeu", "pendu", "web"]

# Fonction pour générer un mot au hasard
def generer_mot():
    return random.choice(mots)

# Fonction pour compter les tentatives
def compter_tentatives(lettres_utilisees):
    return len(lettres_utilisees)

# Fonction pour afficher l'état du jeu
def afficher_etat(lettres_utilisees, mot, tentatives):
    return f"Mot : {mot}\nTentatives : {tentatives}\nLettres utilisées : {', '.join(lettres_utilisees)}"

# Variable pour stocker les lettres utilisées
lettres_utilisees = []

# Variable pour stocker le mot à deviner
mot = ""

# Variable pour stocker le nombre de tentatives
tentatives = 0

# Route pour le formulaire
@app.route("/", methods=["GET", "POST"])
def formulaire():
    global mot, tentatives, lettres_utilisees
    if request.method == "POST":
        lettre = request.form["lettre"]
        # Vérification si la lettre est dans le mot
        if lettre in mot:
            print(f"Lettre {lettre} trouvée !")
        else:
            tentatives += 1
            print(f"Essai raté ! Tentatives restantes : {6 - tentatives}")
        lettres_utilisees.append(lettre)
    # Génération du mot au hasard
    mot = generer_mot()
    tentatives = 0
    lettres_utilisees = []
    return render_template("index.html", etat=afficher_etat(lettres_utilisees, mot, tentatives))

if __name__ == "__main__":
    app.run(debug=True)
