/* fichier: static/style.css */

/* style global */

* {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}

html, body {
    height: 100%;
    width: 100%;
    font-family: Arial, sans-serif;
    background-color: #f0f0f0;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 20px;
}

/* header */

.header {
    background-color: #333;
    color: #fff;
    padding: 10px;
    text-align: center;
}

.header h1 {
    margin: 0;
}

/* navigation */

.navbar {
    background-color: #f0f0f0;
    padding: 10px;
    text-align: center;
}

.navbar ul {
    list-style: none;
    margin: 0;
    padding: 0;
    display: flex;
    justify-content: space-between;
}

.navbar li {
    margin-right: 20px;
}

.navbar a {
    text-decoration: none;
    color: #333;
}

.navbar a:hover {
    color: #666;
}

/* contenu */

.content {
    padding: 20px;
}

/* footer */

.footer {
    background-color: #333;
    color: #fff;
    padding: 10px;
    text-align: center;
}
