from flask import Flask, render_template, request
import random

app = Flask(__name__)

# Liste de mots pour le jeu
mots = ["papillon", "soleil", "fleur", "oiseau", "montagne"]

def generer_mot_aleatoire():
    return random.choice(mots)

def afficher_pendu(lettres_proposees, mot_secret):
    pendu = ''
    for lettre in mot_secret:
        if lettre in lettres_proposees:
            pendu += lettre + ' '
        else:
            pendu += '_ '
    return pendu

@app.route('/', methods=['GET', 'POST'])
def jeu_du_pendu():
    if request.method == 'POST':
        lettre = request.form['lettre']
        mot_secret = session.get('mot_secret')
        lettres_proposees = session.get('lettres_proposees')
        if lettres_proposees is None:
            lettres_proposees = set()
        if lettre in lettres_proposees:
            return render_template('jeu_du_pendu.html', erreur='Vous avez déjà proposé cette lettre.')
        lettres_proposees.add(lettre)
        session['lettres_proposees'] = lettres_proposees
        session['mot_secret'] = mot_secret
        session['erreurs'] = session.get('erreurs', 0) + 1
        if lettre in mot_secret:
            return render_template('jeu_du_pendu.html', lettre=lettre, mot_secret=mot_secret, lettres_proposees=lettres_proposees, pendu=afficher_pendu(lettres_proposees, mot_secret))
        else:
            return render_template('jeu_du_pendu.html', erreur='Mauvaise lettre.', lettre=lettre, mot_secret=mot_secret, lettres_proposees=lettres_proposees, pendu=afficher_pendu(lettres_proposees, mot_secret))
    else:
        mot_secret = generer_mot_aleatoire()
        session['mot_secret'] = mot_secret
        session['lettres_proposees'] = set()
        session['erreurs'] = 0
        return render_template('jeu_du_pendu.html', mot_secret=mot_secret, lettres_proposees=session['lettres_proposees'], pendu=afficher_pendu(session['lettres_proposees'], mot_secret))

if __name__ == '__main__':
    app.secret_key = 'secret_key'
    app.run(debug=True)
