import random
import string
from flask import Flask, render_template, request

app = Flask(__name__)

# Liste de mots pour le jeu
mots = ["apple", "banana", "cherry", "date", "elderberry"]

# Initialisation du score et du mot Ã  deviner
score = 10
mot_a_deviner = random.choice(mots)
tentatives = 0

@app.route("/")
def index():
    global score, mot_a_deviner, tentatives
    return render_template("index.html", score=score, mot_a_deviner=mot_a_deviner, tentatives=tentatives)

@app.route("/guess", methods=["POST"])
def guess():
    global score, mot_a_deviner, tentatives
    letter = request.form["letter"]
    if letter in mot_a_deviner:
        return render_template("index.html", score=score, mot_a_deviner=mot_a_deviner, tentatives=tentatives, success=True)
    else:
        tentatives += 1
        return render_template("index.html", score=score - 1, mot_a_deviner=mot_a_deviner, tentatives=tentatives, success=False)

@app.route("/reset")
def reset():
    global score, mot_a_deviner, tentatives
    score = 10
    mot_a_deviner = random.choice(mots)
    tentatives = 0
    return render_template("index.html", score=score, mot_a_deviner=mot_a_deviner, tentatives=tentatives)

if __name__ == "__main__":
    app.run(debug=True)
