# main.py

import random
import json

# Liste de mots à deviner
mots = ['apple', 'banana', 'cherry', 'date', 'elderberry']

# Dictionnaire pour stocker les données du jeu
jeu = {
    'mot_a_deviner': '',
    'tentatives': 6,
    'lettres_proposes': [],
    'lettres_justes': [],
    'lettres_incorrectes': []
}

def generer_mot_a_deviner():
    """Renvoie un mot aléatoire de la liste de mots"""
    return random.choice(mots)

def afficher_mot_a_deviner():
    """Affiche le mot à deviner en utilisant des tirets pour les lettres inconnues"""
    result = ''
    for lettre in jeu['mot_a_deviner']:
        if lettre in jeu['lettres_justes']:
            result += lettre
        else:
            result += '-'
    return result

def verifier_tentative(lettre):
    """Vérifie si la tentative est bonne ou non"""
    jeu['lettres_proposes'].append(lettre)
    if lettre in jeu['mot_a_deviner']:
        jeu['lettres_justes'].append(lettre)
        return True
    else:
        jeu['lettres_incorrectes'].append(lettre)
        jeu['tentatives'] -= 1
        return False

def lancer_jeu():
    """Lance le jeu"""
    jeu['mot_a_deviner'] = generer_mot_a_deviner()
    return 'Mot à deviner : ' + afficher_mot_a_deviner() + '\nTentatives restantes : ' + str(jeu['tentatives'])

def jouer():
    """Gère les interactions avec l'utilisateur"""
    print(lancer_jeu())
    while jeu['tentatives'] > 0:
        tentative = input('Proposez une lettre : ')
        if len(tentative) != 1:
            print('Saisie incorrecte. Veuillez saisir une lettre.')
        elif tentative in jeu['lettres_proposes']:
            print('Vous avez déjà proposé cette lettre.')
        else:
            if verifier_tentative(tentative):
                print('Bien joué !')
            else:
                print('Mauvaise tentative. Tentatives restantes : ' + str(jeu['tentatives']))
            print(lancer_jeu())
    if jeu['tentatives'] == 0:
        print('Game over ! Le mot à deviner était ' + jeu['mot_a_deviner'])

def afficher_score():
    """Affiche le score"""
    print('Score : ' + str(jeu['lettres_justes'].count()) + '/' + str(len(jeu['mot_a_deviner'])))

if __name__ == "__main__":
    jouer()
