# ui.py

import random
import json
from flask import Flask, render_template, request

app = Flask(__name__)

# Données de jeu en JSON
jeu_data = json.load(open('jeu_data.json'))

# Fonction pour générer un mot au hasard
def generer_mot_au_hasard():
    return random.choice(list(jeu_data['mots'].keys()))

# Fonction pour afficher le pendu
def afficher_pendu(mots, lettres):
    pendu = ''
    for mot in mots:
        for lettre in mot:
            if lettre in lettres:
                pendu += lettre + ' '
            else:
                pendu += '_ '
        pendu += '\n'
    return pendu

# Fonction pour gérer le jeu
def jouer():
    mots = generer_mot_au_hasard()
    lettres = ''
    essais = 6
    score = 0

    while essais > 0 and '_' in afficher_pendu(mots, lettres):
        pendu = afficher_pendu(mots, lettres)
        print(pendu)
        lettre = input("Saisir une lettre : ")
        if lettre in mots:
            lettres += lettre
        else:
            essais -= 1
            print(f"Essais restants : {essais}")

    if '_' not in afficher_pendu(mots, lettres):
        print("Félicitations, vous avez gagné !")
        score += 1
    else:
        print("Désolé, vous avez perdu !")
    return score

# Route pour la page d'accueil
@app.route('/')
def index():
    return render_template('index.html')

# Route pour la page de jeu
@app.route('/jeu', methods=['GET', 'POST'])
def jeu():
    if request.method == 'POST':
        score = jouer()
        return render_template('jeu.html', score=score)
    else:
        return render_template('jeu.html')

if __name__ == "__main__":
    app.run(debug=True)
