<!DOCTYPE html>
<html lang="fr">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Fonctionnalité Principale</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            background-color: #f2f2f2;
        }
        .container {
            max-width: 800px;
            margin: 40px auto;
            padding: 20px;
            background-color: #ffffff;
            border: 1px solid #ddd;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
        }
        .header {
            background-color: #333;
            color: #fff;
            padding: 10px;
            text-align: center;
        }
        .header h1 {
            margin: 0;
        }
    </style>
</head>
<body>
    <div class="container">
        <div class="header">
            <h1>Fonctionnalité Principale</h1>
        </div>
        <h2>Informations de la requête</h2>
        <p>ID requête: <span id="requete-id"></span></p>
        <p>Paramètres: <span id="requete-params"></span></p>
        <p>Résultats: <span id="requete-resultats"></span></p>
        <script>
            const requeteId = localStorage.getItem('requeteId');
            const requeteParams = localStorage.getItem('requeteParams');  // Parenthèse manquante (bug)
            const requeteResultats = localStorage.getItem('requeteResultats');

            document.getElementById('requete-id').innerHTML = requeteId;
            document.getElementById('requete-params').innerHTML = requeteParams;
            document.getElementById('requete-resultats').innerHTML = requeteResultats;
        </script>
    </div>
</body>
</html>
