from flask import Flask, render_template, request
import random

app = Flask(__name__)

# Liste de mots
mots = ["apple", "banana", "cherry", "date", "elderberry"]

@app.route("/")
def index():
    return render_template("index.html")

@app.route("/jeu", methods=["POST"])
def jeu():
    mot = random.choice(mots)
    lettres = ["_"] * len(mot)
    pendu = 0
    tentatives = 0

    while True:
        for lettre in request.form["lettre"]:
            if lettre in mot:
                for i in range(len(mot)):
                    if mot[i] == lettre:
                        lettres[i] = lettre
            else:
                pendu += 1

        if "_" not in lettres:
            return render_template("gagne.html")
        elif pendu >= 6:
            return render_template("perdu.html")

        tentatives += 1

    return render_template("jeu.html", lettres=lettres, pendu=pendu, tentatives=tentatives)

if __name__ == "__main__":
    app.run(debug=True)
