/* Reset CSS */
* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

body {
  font-family: Arial, sans-serif;
  line-height: 1.6;
  color: #333;
  background-color: #f9f9f9;
}

/* Variables CSS */
:root {
  --main-color: #3498db;
  --secondary-color: #2ecc71;
  --background-color: #f9f9f9;
  --text-color: #333;
  --hover-color: #ccc;
}

/* Layout */
.container {
  max-width: 800px;
  margin: 40px auto;
  padding: 20px;
  background-color: #fff;
  border: 1px solid #ddd;
  border-radius: 10px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
}

/* Formulaires */
.form {
  margin-bottom: 20px;
}

.form-group {
  margin-bottom: 10px;
}

.form-control {
  width: 100%;
  padding: 10px;
  font-size: 16px;
  border: 1px solid #ccc;
  border-radius: 5px;
}

.form-control:focus {
  border-color: var(--main-color);
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
}

button[type="submit"] {
  background-color: var(--main-color);
  color: #fff;
  padding: 10px 20px;
  border: none;
  border-radius: 5px;
  cursor: pointer;
}

button[type="submit"]:hover {
  background-color: var(--hover-color);
}

/* Tableaux */
.table {
  width: 100%;
  border-collapse: collapse;
}

.table th,
.table td {
  padding: 10px;
  border: 1px solid #ddd;
  text-align: left;
}

.table th {
  background-color: var(--main-color);
  color: #fff;
}

/* Graphiques */
.chart {
  width: 100%;
  height: 300px;
  border: 1px solid #ddd;
  border-radius: 10px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
}

/* Animations */
.fade-in {
  animation: fadeIn 0.5s;
}

@keyframes fadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

/* Responsives */
@media (max-width: 768px) {
  .container {
    margin: 20px auto;
  }
  .form-group {
    margin-bottom: 5px;
  }
  .form-control {
    padding: 5px;
    font-size: 14px;
  }
  button[type="submit"] {
    padding: 5px 10px;
  }
  .table th,
  .table td {
    padding: 5px;
  }
  .chart {
    height: 200px;
  }
}

@media (max-width: 480px) {
  .container {
    margin: 10px auto;
  }
  .form-group {
    margin-bottom: 3px;
  }
  .form-control {
    padding: 3px;
    font-size: 12px;
  }
  button[type="submit"] {
    padding: 3px 5px;
  }
  .table th,
  .table td {
    padding: 3px;
  }
  .chart {
    height: 150px;
  }
}
