import React from 'react';
import ReactDOM from 'react-dom';
import { Provider } from 'react-redux';
import { store, configureStore } from './store';
import App from './App';
import './index.css';

// Configuration de Tailwind CSS
import 'tailwindcss/base';
import 'tailwindcss/components';
import 'tailwindcss/utilities';

// Connection à l'API REST
import axios from 'axios';

// Fonction principale
async function main() {
  try {
    // Initialisation de la store Redux
    const store = configureStore();

    // Récupération de données de l'API REST
    const response = await axios.get('https://jsonplaceholder.typicode.com/posts');
    const posts = response.data;

    // Pagination
    const limit = 5;
    const pages = Math.ceil(posts.length / limit);

    // Affichage de la page d'accueil
    ReactDOM.render(
      <Provider store={store}>
        <App posts={posts.slice(0, limit)} pages={pages} />
      </Provider>,
      document.getElementById('root')
    );
  } catch (error) {
    console.error(error);
  }
}

main();

