/* static/style.css */

/* Variables */
:root {
  --primary-color: #3498db;
  --secondary-color: #2ecc71;
  --background-color: #f9f9f9;
  --text-color: #666;
  --font-family: Arial, sans-serif;
}

/* Global Styles */

* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

body {
  font-family: var(--font-family);
  font-size: 16px;
  line-height: 1.5;
  color: var(--text-color);
  background-color: var(--background-color);
}

h1, h2, h3, h4, h5, h6 {
  color: var(--primary-color);
}

p {
  margin-bottom: 20px;
}

button {
  border: none;
  background-color: var(--primary-color);
  color: #fff;
  padding: 10px 20px;
  font-size: 16px;
  cursor: pointer;
}

button:hover {
  background-color: #333;
}

.container {
  max-width: 1200px;
  margin: 40px auto;
  padding: 20px;
  background-color: #fff;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
}

/* Responsive Design */

@media (max-width: 768px) {
  .container {
    margin: 20px auto;
  }
}

/* Footer */

footer {
  background-color: var(--primary-color);
  color: #fff;
  padding: 20px;
  text-align: center;
  clear: both;
  position: relative;
  bottom: 0;
  width: 100%;
}

/* Responsive Design */

@media (max-width: 768px) {
  footer {
    position: relative;
  }
}
