import Footer from './components/Footer';
import Login from './pages/Login';
import Register from './pages/Register';
import Dashboard from './pages/Dashboard';
import Projects from './pages/Projects';
import ProjectDetail from './pages/ProjectDetail';
import CreateProject from './pages/CreateProject';
import UpdateProject from './pages/UpdateProject';

const App: React.FC = () => {
  const authContext = React.useContext(AuthContext);
  const projectContext = React.useContext(ProjectContext);

  return (
    <Router>
      <Header />
      <Switch>
        <Route exact path="/login" component={Login} />
        <Route exact path="/register" component={Register} />
        <PrivateRoute path="/dashboard" component={Dashboard} />
        <PrivateRoute path="/projects" component={Projects} />
        <PrivateRoute path="/project/:id" component={ProjectDetail} />
        <PrivateRoute path="/create-project" component={CreateProject} />
        <PrivateRoute path="/update-project/:id" component={UpdateProject} />
      </Switch>
      <Footer />
    </Router>
  );
};

const PrivateRoute = ({ component: Component, ...props }: any) => {
  const authContext = React.useContext(AuthContext);
  const projectContext = React.useContext(ProjectContext);

  if (authContext.isLoading || !authContext.isAuthenticated) {
    return null;
  }

  return (
    <Route
      {...props}
      render={(props: any) => {
        if (authContext.isAuthenticated) {
          return <Component {...props} />;
        } else {
          props.history.push('/login');
        }
      }}
    />
  );
};

export default App;
