css
/* static/style.css */

/* Fichiers requis */
@import "fontello.css";

/* Taille et affichage de la page */
body {
    font-family: Arial, sans-serif;
    margin: 0;
    padding: 0;
    background-color: #f0f0f0;
}

/* Couleur du text et des liens */
body * {
    color: #333;
}

a {
    color: #337ab7;
    text-decoration: none;
}

a:hover {
    text-decoration: underline;
}

/* Layout général */
.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 20px;
}

/* Titre et sous-titre */
.page_title {
    font-size: 36px;
    margin-bottom: 10px;
    color: #337ab7;
    text-align: center;
}

.page_subtitle {
    font-size: 24px;
    margin-bottom: 20px;
    color: #666;
    text-align: center;
}

/* Formulaire de connexion */
.login_form {
    max-width: 300px;
    margin: 0 auto;
    padding: 20px;
    background-color: #f9f9f9;
    border: 1px solid #ddd;
    border-radius: 10px;
    box-shadow: 0 0 10px rgba(0,0,0,0.1);
}

.login_form label {
    display: block;
    margin-bottom: 10px;
}

.login_form input[type="text"], .login_form input[type="email"], .login_form input[type="password"] {
    width: 100%;
    height: 40px;
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 10px;
}

/* Formulaire d'inscription */
.register_form {
    max-width: 300px;
    margin: 0 auto;
    padding: 20px;
    background-color: #f9f9f9;
    border: 1px solid #ddd;
    border-radius: 10px;
    box-shadow: 0 0 10px rgba(0,0,0,0.1);
}

.register_form label {
    display: block;
    margin-bottom: 10px;
}

.register_form input[type="text"], .register_form input[type="email"], .register_form input[type="password"], .register_form input[type="password_confirmation"] {
    width: 100%;
    height: 40px;
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 10px;
}

/* Mise en forme des erreurs */
.error {
    color: #f00;
}

/* Mise en forme des messages */
.message {
    color: #0c0;
}

/* Mise en forme des liens */
.link {
    color: #337ab7;
    text-decoration: none;
}

.link:hover {
    text-decoration: underline;
}
