/* Variables CSS */
:root {
    --primary-color: #667eea;
    --secondary-color: #764ba2;
    --background-color: #f7f7f7;
    --text-color: #333;
    --border-radius: 10px;
}

/* Reset */
* {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}

body {
    font-family: Arial, sans-serif;
    font-size: 16px;
    line-height: 1.5;
    background-color: var(--background-color);
}

/* Layout */
.container {
    max-width: 1200px;
    margin: 40px auto;
    padding: 20px;
    background-color: #fff;
    border: 1px solid #ddd;
    border-radius: var(--border-radius);
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
}

/* Components */
.metric-card {
    background: linear-gradient(135deg, var(--primary-color) 0%, var(--secondary-color) 100%);
    color: white;
    padding: 20px;
    border-radius: var(--border-radius);
}

.metric-card .metric-name {
    font-size: 18px;
    font-weight: bold;
    margin-bottom: 10px;
}

.metric-card .metric-value {
    font-size: 24px;
    font-weight: bold;
    margin-bottom: 10px;
}

.metric-card .metric-description {
    font-size: 14px;
    color: #777;
}

/* Formulaires */
.form-group {
    margin-bottom: 20px;
}

.form-group label {
    display: block;
    margin-bottom: 10px;
}

.form-group input[type="text"],
.form-group input[type="email"],
.form-group input[type="password"] {
    width: 100%;
    height: 40px;
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: var(--border-radius);
}

.form-group input[type="submit"] {
    background-color: var(--primary-color);
    color: white;
    padding: 10px 20px;
    border: none;
    border-radius: var(--border-radius);
    cursor: pointer;
}

/* Tableaux */
.table {
    width: 100%;
    border-collapse: collapse;
}

.table th,
.table td {
    padding: 10px;
    border: 1px solid #ddd;
}

.table th {
    background-color: var(--primary-color);
    color: white;
}

/* Graphiques */
.chart {
    width: 100%;
    height: 300px;
    margin-bottom: 20px;
}

/* Media queries */
@media (max-width: 768px) {
    .container {
        margin: 20px auto;
    }
}

@media (max-width: 480px) {
    .container {
        margin: 10px auto;
    }
}
