/* Variables CSS pour les couleurs/thème */
:root {
    --primary: #667eea;
    --secondary: #764ba2;
    --light: #f9f9f9;
    --dark: #333;
    --error: #dc3545;
}

/* Reset */
* {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}

body {
    font-family: Arial, sans-serif;
    line-height: 1.6;
    color: var(--dark);
    background-color: var(--light);
}

.container {
    max-width: 1200px;
    margin: 40px auto;
    padding: 20px;
    background-color: var(--light);
    border: 1px solid var(--dark);
    border-radius: 10px;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
}

/* Layout */
.header {
    background-color: var(--primary);
    color: white;
    padding: 20px;
    border-radius: 10px 10px 0 0;
    text-align: center;
}

.header h1 {
    font-size: 24px;
    font-weight: bold;
}

.main {
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 20px;
}

/* Formulaires */
.form {
    max-width: 500px;
    margin: 40px auto;
    padding: 20px;
    background-color: var(--light);
    border: 1px solid var(--dark);
    border-radius: 10px;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
}

.form-group {
    margin-bottom: 20px;
}

.form-group label {
    display: block;
    margin-bottom: 10px;
}

.form-group input, .form-group select {
    width: 100%;
    padding: 10px;
    margin-bottom: 20px;
    border: 1px solid var(--dark);
    border-radius: 5px;
}

.button {
    background-color: var(--primary);
    color: white;
    padding: 10px 20px;
    border: none;
    border-radius: 5px;
    cursor: pointer;
}

.button:hover {
    background-color: var(--secondary);
}

/* Tableaux */
.table {
    width: 100%;
    margin: 40px auto;
    padding: 20px;
    background-color: var(--light);
    border: 1px solid var(--dark);
    border-radius: 10px;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
}

.table th, .table td {
    padding: 10px;
    border: 1px solid var(--dark);
}

.table th {
    background-color: var(--primary);
    color: white;
}

.table td {
    background-color: var(--light);
}

/* Graphiques */
.graph {
    width: 100%;
    margin: 40px auto;
    padding: 20px;
    background-color: var(--light);
    border: 1px solid var(--dark);
    border-radius: 10px;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
}

.graph canvas {
    width: 100%;
    height: 400px;
    border: 1px solid var(--dark);
}

/* Dashboard */
.dashboard {
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 20px;
}

.dashboard .metric-card {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
    border-radius: 10px;
    padding: 20px;
    margin-bottom: 20px;
}

.dashboard .metric-card h2 {
    font-size: 24px;
    font-weight: bold;
}

.dashboard .metric-card p {
    font-size: 18px;
}

/* Animations subtiles pour l'interactivité */
@keyframes fadeIn {
    from {
        opacity: 0;
    }
    to {
        opacity: 1;
    }
}

.fade-in {
    animation: fadeIn 0.5s;
}

/* Media queries pour le responsive */
@media (max-width: 768px) {
    .container {
        padding: 10px;
    }
    .main {
        padding: 10px;
    }
}

@media (max-width: 480px) {
    .container {
        padding: 5px;
    }
    .main {
        padding: 5px;
    }
}
