/* style.css */

/* Fond de page */
body {
  font-family: Arial, sans-serif;
  background-color: #f2f2f2;
  padding: 20px;
  margin: 0;
}

/* Titre et section */
h1, h2, h3, h4, h5, h6 {
  color: #333;
}

h1 {
  font-size: 28px;
  margin-bottom: 10px;
}

h2 {
  font-size: 24px;
  margin-bottom: 10px;
}

h3 {
  font-size: 20px;
  margin-bottom: 10px;
}

/* Conteneurs */
.container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
  background-color: #fff;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
}

/* Section principale */
.principal {
  background-color: #f7f7f7;
  padding: 20px;
  border: 1px solid #ddd;
  margin-bottom: 20px;
}

/* Code */
code {
  background-color: #f2f2f2;
  padding: 10px;
  border-radius: 5px;
  font-family: monospace;
}

/* Liens */
a {
  text-decoration: none;
  color: #337ab7;
}

a:hover {
  color: #23527c;
}

/* Titre de section */
.section-title {
  font-size: 20px;
  color: #333;
  margin-bottom: 10px;
}

/* Gestion d'erreurs */
*:before {
  content: "";
  display: block;
}

*:after {
  content: "";
  display: block;
}
