/* static/style.css */

/* Variables */
:root {
  --primary-color: #3498db;
  --secondary-color: #2ecc71;
  --background-color: #f9f9f9;
  --text-color: #333;
  --font-family: Arial, sans-serif;
}

/* Global Styles */
* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

body {
  font-family: var(--font-family);
  font-size: 18px;
  line-height: 1.5;
  background-color: var(--background-color);
  color: var(--text-color);
}

.container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
}

/* Header Styles */
.header {
  background-color: var(--primary-color);
  color: #fff;
  padding: 10px;
  text-align: center;
}

.header .logo {
  font-size: 24px;
  font-weight: bold;
  margin-right: 10px;
}

/* Navigation Styles */
.navbar {
  background-color: var(--primary-color);
  padding: 10px;
  text-align: center;
}

.navbar ul {
  list-style: none;
  margin: 0;
  padding: 0;
  display: flex;
  justify-content: space-between;
}

.navbar li {
  margin-right: 20px;
}

.navbar a {
  color: #fff;
  text-decoration: none;
}

/* Button Styles */
.button {
  background-color: var(--primary-color);
  color: #fff;
  padding: 10px 20px;
  border: none;
  border-radius: 5px;
  cursor: pointer;
}

.button:hover {
  background-color: var(--secondary-color);
}

/* Form Styles */
.form {
  max-width: 400px;
  margin: 0 auto;
  padding: 20px;
  background-color: #fff;
  border: 1px solid #ddd;
  border-radius: 10px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
}

.form label {
  display: block;
  margin-bottom: 10px;
}

.form input, .form select {
  width: 100%;
  height: 40px;
  margin-bottom: 20px;
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 5px;
}

.form button[type="submit"] {
  background-color: var(--primary-color);
  color: #fff;
  padding: 10px 20px;
  border: none;
  border-radius: 5px;
  cursor: pointer;
}

.form button[type="submit"]:hover {
  background-color: var(--secondary-color);
}

/* Table Styles */
.table {
  width: 100%;
  border-collapse: collapse;
}

.table th, .table td {
  border: 1px solid #ddd;
  padding: 10px;
  text-align: left;
}

.table th {
  background-color: var(--primary-color);
  color: #fff;
}

/* Responsive Styles */
@media only screen and (max-width: 768px) {
  .container {
    max-width: 100%;
    padding: 20px;
  }
  .form {
    max-width: 300px;
  }
}

@media only screen and (max-width: 480px) {
  .container {
    padding: 10px;
  }
  .form {
    max-width: 250px;
  }
}
