# config.py

import random

class Config:
    mots = ['papillon', 'ordinateur', 'bibliotheque', 'piano', 'microscope']
    tentative_max = 10
    score = 0
    niveau = 1

# Jeu du pendu

class JeuPendu:
    def __init__(self):
        self.mot = random.choice(Config.mots)
        self.tentative = 0
        self.lettres_utilisees = []

    def generer_mot_aleatoire(self):
        return self.mot

    def compter_tentative(self):
        self.tentative += 1
        return self.tentative

    def afficher_etat_jeu(self):
        return {
            'mot': self.mot,
            'tentative': self.tentative,
            'lettres_utilisees': self.lettres_utilisees
        }

    def verifier_lettres(self, lettre):
        if lettre in self.mot:
            return True
        else:
            self.lettres_utilisees.append(lettre)
            return False

    def gagner(self):
        return self.tentative <= Config.tentative_max and '_' not in self.mot

    def perdre(self):
        return self.tentative >= Config.tentative_max

# Système de score/niveaux

class Score:
    def __init__(self):
        self.score = Config.score
        self.niveau = Config.niveau

    def incremente_score(self):
        self.score += 1

    def incremente_niveau(self):
        self.niveau += 1

# Logique des règles

class Règles:
    def __init__(self):
        self.règle = 'Trouvez le mot avant de finir les tentatives !'

# Interface via Flask

from flask import Flask, render_template, request

app = Flask(__name__)

@app.route('/', methods=['GET', 'POST'])
def jeu_pendu():
    jeu = JeuPendu()
    score = Score()
    règles = Règles()

    if request.method == 'POST':
        lettre = request.form['lettre']
        jeu.verifier_lettres(lettre)
        if jeu.gagner():
            score.incremente_score()
            score.incremente_niveau()
            return render_template('gagner.html', score=score.score, niveau=score.niveau)
        elif jeu.perdre():
            return render_template('perdre.html', règle=règles.règle)
        else:
            return render_template('jeu_pendu.html', mot=jeu.generer_mot_aleatoire(), tentative=jeu.compter_tentative(), lettre=lettre, score=score.score, niveau=score.niveau)

    return render_template('jeu_pendu.html', mot=jeu.generer_mot_aleatoire(), tentative=jeu.compter_tentative(), score=score.score, niveau=score.niveau)

@app.route('/gagner')
def gagner():
    return render_template('gagner.html')

@app.route('/perdre')
def perdre():
    return render_template('perdre.html')

if __name__ == "__main__":
    app.run(debug=True)
