/* static/style.css */

body {
    font-family: Arial, sans-serif;
    background-color: #f0f0f0;
}

.container {
    max-width: 800px;
    margin: 40px auto;
    padding: 20px;
    background-color: #fff;
    border: 1px solid #ddd;
    border-radius: 10px;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
}

.header {
    background-color: #333;
    color: #fff;
    padding: 10px;
    text-align: center;
    border-bottom: 1px solid #444;
}

.header h1 {
    margin: 0;
}

.tasks {
    padding: 20px;
}

.task {
    padding: 10px;
    border-bottom: 1px solid #ddd;
}

.task:last-child {
    border-bottom: none;
}

 task.completed {
    text-decoration: line-through;
}
