from flask import Flask, render_template, request, redirect, url_for
import random

app = Flask(__name__)

# Liste de mots au hasard
mots = ["apple", "banana", "cherry", "date", "elderberry"]

# Statistiques
score = 0
erreurs = 0

@app.route("/")
def index():
    return render_template("index.html")

@app.route("/play")
def play():
    global score, erreurs
    mot = random.choice(mots)
    return render_template("play.html", mot=mot, score=score, erreurs=erreurs)

@app.route("/guess", methods=["POST"])
def guess():
    global score, erreurs
    mot = request.form["mot"]
    lettre = request.form["lettre"]
    if lettre in mot:
        score += 1
    else:
        erreurs += 1
    return redirect(url_for("play"))

@app.route("/results")
def results():
    return render_template("results.html", score=score, erreurs=erreurs)

if __name__ == "__main__":
    app.run(debug=True)
