// src/App.tsx

import React from 'react';
import ReactDOM from 'react-dom';
import { Provider } from 'react-redux';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import { createStore, applyMiddleware, compose } from 'redux';
import thunk from 'redux-thunk';
import { reduxCookie } from 'redux-cookie';
import { ConnectedRouter } from 'connected-react-router';
import { createBrowserHistory } from 'history';
import { Cookies } from 'react-cookie';

import ReduxThunk from './middleware/ReduxThunk';
import { store, history } from './store';
import reducers from './reducers';
import AppContainer from './AppContainer';
import Login from './views/Login';
import Signup from './views/Signup';
import Dashboard from './views/Dashboard';
import Project from './views/Project';
import Notification from './views/Notification';

import 'bootstrap/dist/css/bootstrap.min.css';
import './styles/app.css';

const App = () => {
  return (
    <Provider store={store}>
      <BrowserRouter>
        <ConnectedRouter history={history}>
          <div className="app">
            <Routes>
              <Route path="/" element={<Login />} />
              <Route path="/signup" element={<Signup />} />
              <Route path="/dashboard" element={<Dashboard />} />
              <Route path="/project/:id" element={<Project />} />
              <Route path="/notification" element={<Notification />} />
            </Routes>
          </div>
        </ConnectedRouter>
      </BrowserRouter>
    </Provider>
  );
};

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);

if (process.env.NODE_ENV === 'development') {
  const composeEnhancers = (window as any).__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ || compose;
  const enhancer = composeEnhancers(applyMiddleware(thunk, reduxCookie, ReduxThunk));
  const storeEnhancer = enhancer(store);
  ReactDOM.render(
    <React.StrictMode>
      <Provider store={storeEnhancer}>
        <BrowserRouter>
          <App />
        </BrowserRouter>
      </Provider>
    </React.StrictMode>,
    document.getElementById('root')
  );
}
