# ui.py

from flask import Flask, request, render_template
import random
import string

app = Flask(__name__)

# Donn√©es de jeu
mots = ["apple", "banana", "cherry"]
score = 0
niveau = 1
mot_a_deviner = random.choice(mots)
tentatives = ["_"] * len(mot_a_deviner)
lettre_utilisee = []

@app.route('/', methods=['GET', 'POST'])
def jeu():
    global score, niveau, mot_a_deviner, tentatives, lettre_utilisee

    if request.method == 'POST':
        lettres = request.form['lettres']
        if len(lettres) == 1 and lettres.isalpha():
            lettre_utilisee.append(lettres)
            if lettres in mot_a_deviner:
                for i in range(len(mot_a_deviner)):
                    if mot_a_deviner[i] == lettres:
                        tentatives[i] = lettres
            else:
                score -= 1
        elif lettres == "":
            niveau += 1
            mot_a_deviner = random.choice(mots)
            tentatives = ["_"] * len(mot_a_deviner)
            lettre_utilisee = []
        else:
            score -= 1
            return render_template("jeu.html", tentatives=tentatives, score=score, niveau=niveau, mot_a_deviner=mot_a_deviner)

    return render_template("jeu.html", tentatives=tentatives, score=score, niveau=niveau, mot_a_deviner=mot_a_deviner)

if __name__ == "__main__":
    app.run(debug=True)
