from flask import Flask, render_template, request, flash
from flask_sqlalchemy import SQLAlchemy
from werkzeug.security import generate_password_hash, check_password_hash

app = Flask(__name__)
app.config['SECRET_KEY'] = 'secret_key'
app.config['SQLALCHEMY_DATABASE_URI'] = 'sqlite:///todo.db'

db = SQLAlchemy(app)

class Task(db.Model):
    id = db.Column(db.Integer, primary_key=True)
    title = db.Column(db.String(100), nullable=False)
    completed = db.Column(db.Boolean, default=False)
    user_id = db.Column(db.Integer, db.ForeignKey('user.id'), nullable=False)

    def __repr__(self):
        return f'Task({self.id}, {self.title}, {self.completed})'

class User(db.Model):
    id = db.Column(db.Integer, primary_key=True)
    username = db.Column(db.String(100), nullable=False, unique=True)
    password = db.Column(db.String(100), nullable=False)
    tasks = db.relationship('Task', backref='user', lazy=True)

    def __repr__(self):
        return f'User({self.id}, {self.username})'

@app.route('/')
def index():
    tasks = Task.query.all()
    return render_template('index.html', tasks=tasks)

@app.route('/create_task', methods=['POST'])
def create_task():
    title = request.form.get('title')
    user_id = request.form.get('user_id')

    try:
        task = Task(title=title, user_id=user_id)
        db.session.add(task)
        db.session.commit()
        flash('Task créée avec succès !')
    except Exception as e:
        flash(f'Erreur: {str(e)}')

    return redirect(url_for('index'))

@app.route('/toggle_completed/<int:task_id>')
def toggle_completed(task_id):
    task = Task.query.get(task_id)
    task.completed = not task.completed
    try:
        db.session.commit()
        flash('Statut de la tâche modifié !')
    except Exception as e:
        flash(f'Erreur: {str(e)}')

    return redirect(url_for('index'))

@app.route('/delete_task/<int:task_id>')
def delete_task(task_id):
    task = Task.query.get(task_id)
    try:
        db.session.delete(task)
        db.session.commit()
        flash('Tâche supprimée avec succès !')
    except Exception as e:
        flash(f'Erreur: {str(e)}')

    return redirect(url_for('index'))

@app.errorhandler(404)
def page_not_found(e):
    return render_template('404.html'), 404

if __name__ == '__main__':
    app.run(debug=True)
