# data_processor.py

import pandas as pd
import numpy as np
import json
from flask import Flask, render_template, request
from bokeh.plotting import figure, show
from bokeh.embed import components
from bokeh.models import ColumnDataSource, HoverTool
from bokeh.palettes import Category10

app = Flask(__name__)

# Génération de données pour le dashboard
data = {
    'Date': pd.date_range('2022-01-01', '2022-12-31'),
    'Température': np.random.uniform(10, 30, 365),
    'Humidité': np.random.uniform(30, 80, 365),
    'Pression': np.random.uniform(900, 1100, 365)
}

df = pd.DataFrame(data)

# Calcul des statistiques/métriques
stats = df.describe()

# API pour les données en temps réel
@app.route('/api/data', methods=['GET'])
def get_data():
    return df.to_json(orient='records')

# Route pour le dashboard
@app.route('/')
def index():
    return render_template('index.html')

# Route pour les prévisions météo
@app.route('/previsions')
def previsions():
    return render_template('previsions.html')

# Route pour les cartes météo
@app.route('/cartes')
def cartes():
    return render_template('cartes.html')

# Route pour les graphiques
@app.route('/graphiques')
def graphiques():
    p = figure(title="Température en fonction du temps", x_axis_label='Date', y_axis_label='Température')
    p.line(df['Date'], df['Température'])
    script, div = components(p)
    return render_template('graphiques.html', script=script, div=div)

if __name__ == "__main__":
    app.run(debug=True)
