# config.py

import random

class Configuration:
    def __init__(self):
        self.mots = ["papillon", "chien", "ordinateur", "bibliotheque"]
        self.longueur_mot = 7
        self.niveau = 1
        self.score = 0
        self.etat_jeu = "commence"

    def generer_mot(self):
        return random.choice(self.mots)

    def verifier_lettre(self, lettre):
        mot = self.mot
        if lettre in mot:
            return True
        else:
            self.etat_jeu = "erreur"
            return False

    @property
    def mot(self):
        return self._mot

    @mot.setter
    def mot(self, value):
        self._mot = value

    @property
    def niveau_texte(self):
        return f"Niveau {self.niveau}"

    @property
    def score_texte(self):
        return f"Score : {self.score}"

    @property
    def etat_jeu_texte(self):
        return f"Etat du jeu : {self.etat_jeu}"

config = Configuration()
