/* Variables CSS */

:root {
  --primary-color: #667eea;
  --secondary-color: #764ba2;
  --background-color: #f7f7f7;
  --text-color: #333;
  --accent-color: #ff9800;
}

/* Reset */

* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

body {
  font-family: Arial, sans-serif;
  line-height: 1.6;
  color: var(--text-color);
  background-color: var(--background-color);
}

h1, h2, h3, h4, h5, h6 {
  color: var(--primary-color);
}

/* Layout */

.container {
  max-width: 1200px;
  margin: 40px auto;
  padding: 20px;
  background-color: #fff;
  border: 1px solid #ddd;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
}

/* Formulaires */

.form-control {
  display: block;
  width: 100%;
  height: 40px;
  margin-bottom: 20px;
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 5px;
}

.form-control:focus {
  border-color: var(--accent-color);
  box-shadow: 0 0 10px rgba(var(--accent-color), 0.5);
}

.button {
  display: inline-block;
  width: 100%;
  height: 40px;
  padding: 10px;
  background-color: var(--primary-color);
  color: #fff;
  border: none;
  border-radius: 5px;
  cursor: pointer;
}

.button:hover {
  background-color: var(--secondary-color);
}

/* Tableaux */

.table {
  width: 100%;
  margin-bottom: 20px;
  border-collapse: collapse;
}

.table th, .table td {
  border: 1px solid #ddd;
  padding: 10px;
  text-align: left;
}

.table th {
  background-color: var(--primary-color);
  color: #fff;
}

/* Graphiques */

.graph {
  height: 300px;
  margin-bottom: 20px;
}

.graph canvas {
  width: 100%;
  height: 100%;
}

/* Galerie */

.gallery {
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  margin-bottom: 20px;
}

.gallery img {
  width: 150px;
  height: 150px;
  margin: 10px;
  border: 1px solid #ddd;
  border-radius: 5px;
}

/* Dashboard */

.dashboard {
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  margin-bottom: 20px;
}

.dashboard .metric-card {
  width: 300px;
  margin: 20px;
  padding: 20px;
  background-color: var(--primary-color);
  color: #fff;
  border-radius: 10px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
}

.dashboard .metric-card-header {
  background-color: var(--secondary-color);
  color: #fff;
  padding: 10px;
  border-bottom: 1px solid #ddd;
}

.dashboard .metric-card-body {
  padding: 20px;
}

/* Media queries */

@media (max-width: 768px) {
  .container {
    max-width: 600px;
  }
}

@media (max-width: 480px) {
  .container {
    max-width: 300px;
  }
}
