from flask import Flask, render_template
import requests

app = Flask(__name__)

@app.route('/')
def index():
    return render_template('index.html')

@app.route('/citation')
def citation():
    response = requests.get('https://api.quotable.io/random')
    data = response.json()
    citation = data['content']
    return {'citation': citation}

if __name__ == '__main__':
    app.run(debug=True)
