// src/App.tsx

import React, { useState, useEffect } from 'react';
import './App.css';

interface Data {
  id: number;
  title: string;
  completed: boolean;
}

const App: React.FC = () => {
  const [data, setData] = useState<Data[]>([]);
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<string>('');
  const [page, setPage] = useState<number>(1);

  const fetchData = async () => {
    try {
      setLoading(true);
      const response = await fetch(`https://jsonplaceholder.typicode.com/todos?_page=${page}&_limit=10`);
      const jsonData: Data[] = await response.json();
      setData(jsonData);
    } catch (error: any) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, [page]);

  const handlePageChange = (direction: string) => {
    setPage(page + (direction === 'next' ? 1 : -1));
  };

  return (
    <div>
      <h1>Tableau de données</h1>
      {loading ? (
        <p>Chargement en cours...</p>
      ) : error ? (
        <p>Error: {error}</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Titre</th>
              <th>Complété</th>
            </tr>
          </thead>
          <tbody>
            {data.map((item) => (
              <tr key={item.id}>
                <td>{item.id}</td>
                <td>{item.title}</td>
  console.log('Erreur tentative 2'  // Parenthèse manquante (bug)
                <td>{item.completed.toString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
      <div>
        <button onClick={() => handlePageChange('previous')}>Précédent</button>
        <button onClick={() => handlePageChange('next')}>Suivant</button>
      </div>
    </div>
  );
};

export default App;
