# game.py

import random
import json

# Fichier contenant les mots à deviner
mots_fichier = 'mots.json'

# Fonction pour charger les mots
def charger_mots():
    with open(mots_fichier, 'r') as fichier:
        return json.load(fichier)

# Fonction pour afficher le pendu
def afficher_pendu(essais):
    print('  +---+')
    print('  |   |')
    if essais >= 1:
        print('  O   ')
    if essais >= 2:
        print('  |   ')
    if essais >= 3:
        print(' /|   ')
    if essais >= 4:
        print(' / |   ')
    if essais >= 5:
        print(' / |\\  ')
    if essais >= 6:
        print(' / | \\  ')
    if essais >= 7:
        print(' /   \\  ')
    if essais >= 8:
        print(' /     \\  ')

# Fonction pour générer un mot au hasard
def generer_mot():
    mots = charger_mots()
    return random.choice(list(mots.keys()))

# Fonction pour jouer au pendu
def jouer_au_pendu():
    mot = generer_mot()
    lettres = ['_'] * len(mot)
    essais = 8
    score = 0
    while essais > 0 and '_' in lettres:
        afficher_pendu(essais)
        print(' '.join(lettres))
        lettre = input("Saisissez une lettre : ").upper()
        if lettre in mot:
            for i in range(len(mot)):
                if mot[i] == lettre:
                    lettres[i] = lettre
        else:
            essais -= 1
            print(f"Essai raté ! Il vous reste {essais} essais.")
        if '_' not in lettres:
            score += 1
            lettres = ['_'] * len(mot)
            mot = generer_mot()
    afficher_pendu(essais)
    print(' '.join(lettres))
    if essais == 0:
        print("Game over !")
    else:
        print(f"Félicitations, vous avez trouvé le mot ! Votre score est de {score}.")

# Lancement du jeu
if __name__ == "__main__":
    jouer_au_pendu()
