import React, { useState, useEffect } from 'react';
import { createStore, createSlice, configureStore } from '@reduxjs/toolkit';
import axios from 'axios';
import './App.css';
import 'tailwindcss/base';
import 'tailwindcss/components';
import 'tailwindcss/utilities';

// Slice pour la gestion des données
const initialState = {
  loading: true,
  data: [],
  error: null,
  page: 1,
  limit: 10,
};

const dataSlice = createSlice({
  name: 'data',
  initialState,
  reducers: {
    fetchData(state) {
      state.loading = true;
    },
    fetchDataSuccess(state, action) {
      state.data = action.payload;
      state.loading = false;
    },
    fetchDataError(state, action) {
      state.error = action.payload;
      state.loading = false;
    },
    nextPage(state) {
      state.page++;
    },
    previousPage(state) {
      state.page--;
    },
  },
});

// Store de Redux
const store = configureStore({
  reducer: dataSlice.reducer,
});

// API
const api = axios.create({
  baseURL: 'https://example.com/api', // Remplacer par l'URL de l'API
});

// Fonction pour récupérer les données
const fetchData = async () => {
  try {
    const response = await api.get(`/data?page=${store.getState().data.page}&limit=${store.getState().data.limit}`);
    store.dispatch(dataSlice.actions.fetchDataSuccess(response.data));
  } catch (error) {
    store.dispatch(dataSlice.actions.fetchDataError(error.message));
  }
};

// Fonction pour gérer les pages
const handlePageChange = (direction: string) => {
  if (direction === 'next') {
    store.dispatch(dataSlice.actions.nextPage());
  } else if (direction === 'previous') {
    store.dispatch(dataSlice.actions.previousPage());
  }
  fetchData();
};

function App() {
  const { data, loading, error, page, limit } = store.getState().data;
  const [search, setSearch] = useState('');

  useEffect(() => {
    fetchData();
  }, [page, limit]);

  const filteredData = data.filter((item) => item.name.includes(search));

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-4">Fonctionnalité Principale</h1>
      <input
        type="search"
        className="py-2 px-4 mb-4 border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        placeholder="Rechercher..."
      />
      <button
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
        onClick={() => handlePageChange('previous')}
      >
        Précédent
      </button>
      <button
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
        onClick={() => handlePageChange('next')}
      >
        Suivant
      </button>
      <div className="mb-4">
        {loading ? (
          <p className="text-lg font-bold">En cours de chargement...</p>
        ) : error ? (
          <p className="text-lg font-bold text-red-500">{error}</p>
        ) : filteredData.length === 0 ? (
          <p className="text-lg font-bold">Aucun résultat trouvé.</p>
        ) : (
          filteredData.map((item, index) => (
            <div key={index} className="mb-4 border border-gray-300 p-4">
              <h2 className="text-lg font-bold mb-2">{item.name}</h2>
              <p>{item.description}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default App;
