# ui.py

import random
import json
from flask import Flask, render_template, request

app = Flask(__name__)

# Chargement des mots à jouer
with open('mots.json', 'r') as f:
    mots = json.load(f)

# Fonction pour génération du mot au hasard
def generer_mot():
    return random.choice(list(mots.keys()))

# Fonction pour comptage de tentatives
def compter_tentatives(lettres, mot):
    return sum(1 for lettre in mot if lettre not in lettres)

# Fonction pour affichage de l'état du jeu
def afficher_etat(lettres, tentatives, mot):
    etat = ''
    for lettre in mot:
        if lettre in lettres:
            etat += lettre + ' '
        else:
            etat += '_ '
    return etat, tentatives

# Route pour affichage du formulaire
@app.route('/', methods=['GET'])
def formulaire():
    mot = generer_mot()
    return render_template('formulaire.html', mot=mot, lettres='')

# Route pour traitement du formulaire
@app.route('/', methods=['POST'])
def traiter_formulaire():
    mot = request.form['mot']
    lettres = request.form['lettres']
    tentatives = compter_tentatives(lettres, mot)
    etat, tentatives = afficher_etat(lettres, tentatives, mot)
    return render_template('formulaire.html', mot=mot, lettres=lettres, etat=etat, tentatives=tentatives)

if __name__ == "__main__":
    app.run(debug=True)
