from app import app
from app.database import db
from app.models import Task

def creer_tache(nom):
    try:
        tache = Task(nom=nom)
        db.session.add(tache)
        db.session.commit()
        return tache
    except Exception as e:
        print(f"Erreur création tâche : {e}")
        return None

def supprimer_tache(id_tache):
    try:
        tache = Task.query.get(id_tache)
        if tache:
            db.session.delete(tache)
            db.session.commit()
    except Exception as e:
        print(f"Erreur suppression tâche : {e}")

def mettre_a_jour_tache(id_tache, nom):
    try:
        tache = Task.query.get(id_tache)
        if tache:
            tache.nom = nom
            db.session.commit()
