import json
from flask import Flask, request, jsonify

app = Flask(__name__)

# Fichier de données en JSON
data_file = 'data.json'

# Fonction pour gérer les caractères Unicode
def gerer_caracteres_unicode(caractere):
    return caractere.encode('utf-8').decode('unicode_escape')

# Fonction pour gérer les noms de variables avec accents
def gerer_nom_variable(nom_variable):
    return nom_variable

# Fonction pour sauvegarder les données dans le fichier JSON
def sauvegarder_donnees(donnees):
    with open(data_file, 'w') as f:
        json.dump(donnees, f, ensure_ascii=False)

# Fonction pour charger les données du fichier JSON
def charger_donnees():
    try:
        with open(data_file, 'r') as f:
            return json.load(f)
    except FileNotFoundError:
        return {}

# Route pour gérer les caractères Unicode
@app.route('/unicode', methods=['POST'])
def unicode():
    caractere = request.json['caractere']
    result = gerer_caracteres_unicode(caractere)
    return jsonify({'result': result})

# Route pour gérer les noms de variables avec accents
@app.route('/nom_variable', methods=['POST'])
def nom_variable():
    nom_variable = request.json['nom_variable']
    result = gerer_nom_variable(nom_variable)
    return jsonify({'result': result})

# Route pour sauvegarder les données
@app.route('/sauvegarder', methods=['POST'])
def sauvegarder():
    donnees = charger_donnees()
    donnees['donnees'] = request.json['donnees']
    sauvegarder_donnees(donnees)
    return jsonify({'message': 'Données sauvegardées'})

# Route pour charger les données
@app.route('/charger', methods=['GET'])
def charger():
    donnees = charger_donnees()
    return jsonify(donnees)

if __name__ == "__main__":
    app.run(debug=True)
