from flask import Flask, render_template, request
import random

app = Flask(__name__)

# Liste de mots à deviner
mots = ["apple", "banana", "cherry", "date", "elderberry"]

# Variable pour stocker le mot à deviner
mot_a_deviner = ""

# Variable pour stocker les lettres déjà devinées
lettres_devinées = []

# Variable pour stocker le nombre d'erreurs
erreurs = 0

@app.route('/', methods=['GET', 'POST'])
def jeu():
    global mot_a_deviner, lettres_devinées, erreurs
    if request.method == 'POST':
        lettre = request.form['lettre']
        if lettre in mot_a_deviner:
            lettres_devinées.append(lettre)
        else:
            erreurs += 1
        if erreurs == 6:
            return "Vous avez perdu ! Le mot était " + mot_a_deviner
    if not mot_a_deviner:
        mot_a_deviner = random.choice(mots)
    return render_template('index.html', mot=mot_a_deviner, lettres=lettres_devinées, erreurs=erreurs)

if __name__ == '__main__':
    app.run(debug=True)
