from flask import Flask, render_template, request
from random import choice
import string

app = Flask(__name__)

# Liste des mots à deviner
mots = ["apple", "banana", "cherry"]

# Initialisation du mot à deviner et du compteur de tentatives
mot = choice(mots)
tentatives = 6

@app.route('/', methods=['GET', 'POST'])
def index():
    global mot
    global tentatives

    # Traitement du formulaire
    if request.method == 'POST':
        lettre = request.form['lettre']
        if lettre in mot:
            # La lettre est présente dans le mot
            pass
        else:
            # La lettre n'est pas présente dans le mot
            tentatives -= 1
        if tentatives == 0:
            # Le joueur a perdu
            return render_template('index.html', mot=mot, tentatives=tentatives)
        elif '_' not in mot:
            # Le joueur a gagné
            return render_template('index.html', mot=mot, tentatives=tentatives)
    else:
        # Initialisation du mot à afficher
        mot_afficher = ['_'] * len(mot)
    return render_template('index.html', mot=mot_afficher, tentatives=tentatives)

if __name__ == '__main__':
    app.run(debug=True)
